import React, { useState, useEffect, useRef } from 'react';
import mapboxgl from 'mapbox-gl';

interface MapProps {}

const Map: React.FC<MapProps> = ({}: MapProps) => {
  mapboxgl.accessToken =
    'pk.eyJ1Ijoic2VzY2h3YXJ0ejgiLCJhIjoiY2s5aW83cXNxMWJ1MTNrbGxzaW55YmFqbCJ9.Wcju4UV4OLz90abpY6HtuA';

  const [lng, setLng] = useState(34);
  const [lat, setLat] = useState(5);
  const [zoom, setZoom] = useState(2);
  const containerRef = React.useRef();

  useEffect(() => {
    const finalMap = new mapboxgl.Map({
      container: 'mapDiv',
      style: 'mapbox://styles/mapbox/streets-v11',
      center: [lng, lat],
    });
  }, []);

  // const finalMap = new mapboxgl.Map({
  //   container: 'mapDiv',
  //   style: 'mapbox://styles/seschwartz8/ck9ioaqt300q41jqpgdm3ciln',
  //   zoom: 0.5,
  //   minZoom: 0.5,
  //   maxZoom: 12,
  //   center: [-100, 35],
  // });

  return <div ref={containerRef}>{finalMap}</div>;
};

export default Map;
