import React from 'react';
import mapboxgl from 'mapbox-gl';
import styled from 'styled-components';

const MapContainer = styled.div`
  position: absolute;
  top: 0;
  right: 0;
  left: 0;
  bottom: 0;
`;

const MapSideBar = styled.div`
  display: inline-block;
  position: absolute;
  top: 0;
  left: 0;
  margin: 12px;
  background-color: #404040;
  color: #ffffff;
  z-index: 1 !important;
  padding: 6px;
  font-weight: bold;
`;

interface MapProps {}

interface MapState {
  lng: number;
  lat: number;
  zoom: number;
  mapContainer: HTMLDivElement | null;
}

mapboxgl.accessToken =
  'pk.eyJ1Ijoic2VzY2h3YXJ0ejgiLCJhIjoiY2s5aW83cXNxMWJ1MTNrbGxzaW55YmFqbCJ9.Wcju4UV4OLz90abpY6HtuA';

class Map extends React.Component<MapProps, MapState> {
  constructor(props: MapProps) {
    super(props);

    this.state = { lng: 5, lat: 34, zoom: 2, mapContainer: null };
  }

  componentDidMount() {
    const finalMap = new mapboxgl.Map({
      container: this.mapContainer,
      style: 'mapbox://styles/mapbox/streets-v11',
      center: [this.state.lng, this.state.lat],
      zoom: this.state.zoom,
    });

    finalMap.on('move', () => {
      this.setState({
        lng: parseFloat(finalMap.getCenter().lng.toFixed(4)),
        lat: parseFloat(finalMap.getCenter().lat.toFixed(4)),
        zoom: parseFloat(finalMap.getZoom().toFixed(2)),
      });
    });
  }

  render() {
    return (
      <div>
        <MapSideBar>
          Longitude: {this.state.lng} | Latitude: {this.state.lat} | Zoom:{' '}
          {this.state.zoom}
        </MapSideBar>
        <MapContainer
          ref={(
            el: React.DetailedHTMLProps<
              React.HTMLAttributes<HTMLDivElement>,
              HTMLDivElement
            >
          ) => (this.mapContainer = el)}
        />
      </div>
    );
  }
}

export default Map;
