import React, { useContext } from 'react';
import Card from './Card';
import { Country } from './App';
import styled from 'styled-components';
import ModeContext from '../contexts/mode';

const CardListContainer = styled.div`
  display: flex;
  justify-content: space-around;
  flex-wrap: wrap;
  padding: 5%;

  ${({ mode = 'light' }: CardListContainerProps) =>
    mode === `light`
      ? `
    background-color: #ededed;
    `
      : `
    background-color: #212E37;
`};
`;

interface CardListContainerProps {
  mode: string;
}

interface CardListProps {
  countries?: Country[];
}

const CardList: React.FC<CardListProps> = ({
  countries = [],
}: CardListProps) => {
  const mode = useContext(ModeContext);

  const renderCards = () => {
    const cardsHTML = countries.map((country) => {
      return (
        <Card
          key={country.id}
          imgUrl={country.imgUrl}
          name={country.name}
          population={country.population}
          region={country.region}
          capital={country.capital}
        />
      );
    });
    return cardsHTML;
  };

  return <CardListContainer mode={mode}>{renderCards()}</CardListContainer>;
};

export default CardList;
