import React from 'react';
import mapboxgl from 'mapbox-gl';
import styled from 'styled-components';

const MapContainer = styled.div`
  position: absolute;
  top: 0;
  right: 0;
  left: 0;
  bottom: 0;
`;

// const finalMap = new mapboxgl.Map({
//   container: 'mapDiv',
//   style: 'mapbox://styles/seschwartz8/ck9ioaqt300q41jqpgdm3ciln',
//   zoom: 0.5,
//   minZoom: 0.5,
//   maxZoom: 12,
//   center: [-100, 35],
// });

interface MapProps {}

mapboxgl.accessToken =
  'pk.eyJ1Ijoic2VzY2h3YXJ0ejgiLCJhIjoiY2s5aW83cXNxMWJ1MTNrbGxzaW55YmFqbCJ9.Wcju4UV4OLz90abpY6HtuA';

class Map extends React.Component<MapProps, MapState> {
  constructor(props: MapProps) {
    super(props);

    this.state = { lng: 5, lat: 34, zoom: 2 };
  }

  componentDidMount() {
    const finalMap = new mapboxgl.Map({
      container: this.mapContainer,
      style: 'mapbox://styles/mapbox/streets-v11',
      center: [this.state.lng, this.state.lat],
      zoom: this.state.zoom,
    });

    finalMap.on('move', () => {
      this.setState({
        lng: finalMap.getCenter().lng.toFixed(4),
        lat: finalMap.getCenter().lat.toFixed(4),
        zoom: finalMap.getZoom().toFixed(2),
      });
    });
  }

  render() {
    return (
      <div>
        <div>
          Longitude: {this.state.lng} | Latitude: {this.state.lat} | Zoom:{' '}
          {this.state.zoom}
        </div>
        <MapContainer ref={(el) => (this.mapContainer = el)} />
      </div>
    );
  }
}

export default Map;
