import React, { useContext, useState } from 'react';
import styled from 'styled-components';
import ModeContext from '../contexts/mode';
import { useSpring, animated as animate } from 'react-spring';

interface CardProps {
  imgUrl: string;
  name: string;
  population: string;
  region: string;
  capital: string;
}

const Card: React.FC<CardProps> = ({
  imgUrl,
  name,
  population,
  region,
  capital,
}: CardProps) => {
  const mode = useContext(ModeContext);
  const [flipped, setFlipped] = useState(false);

  const { transform, opacity } = useSpring({
    opacity: flipped ? 1 : 0,
    transform: `perspective(600px) rotateX(${flipped ? 180 : 0}deg)`,
    config: { mass: 5, tension: 500, friction: 80 },
  });

  return (
    <CardContainer onClick={() => setFlipped((flipped) => !flipped)}>
      <CardBack transform={transform} opacity={opacity}>
        BACK
      </CardBack>

      <CardFront mode={mode} transform={transform} opacity={opacity}>
        <img src={imgUrl} alt={name}></img>
        <div>
          <h3>{name}</h3>
          <p>
            <b>Population: </b> {population}
          </p>
          <p>
            <b>Region: </b> {region}
          </p>
          <p>
            <b>Capital: </b> {capital}
          </p>
        </div>
      </CardFront>
    </CardContainer>
  );
};

export default Card;

// Styled component
const CardContainer = styled.div`
  border-radius: 5px;
  margin: 2%;
  max-width: 250px;
`;

const CardFront = styled.div`
  & div {
    margin: 5% 0;
    padding: 10%;
  }

  & img {
    max-width: 250px;
  }

  ${({ mode = 'light' }: CardSideProps) =>
    mode === `light`
      ? `
      background-color: white;
      color: black;
      box-shadow: 0 0 8px #d9d9d9;
      `
      : `
      background-color: #324354;
      color: white;
      box-shadow: 0 0 8px #1D2A36;
      `};

  ${({ opacity, transform }: CardSideProps) =>
    `opacity: ${opacity};
    transform: ${transform.interpolate((t: any) => `${t} rotateX(180deg)`)};`}
`;

const CardBack = styled.div`
  ${({ opacity, transform }: CardSideProps) =>
    `opacity: ${opacity.interpolate((o: any) => 1 - o)};
    transform: ${transform};`}
`;

interface CardSideProps {
  mode: string;
  opacity: string;
  transform: any;
}
